function add(){var e=document.getElementById("input").value,n='<a  class="collection-item"><input type="checkbox" class="filled-in task" id="task'+nb+'" /><label for="task'+nb+'">'+e+'</label><span class=" date">'+Date();liste.innerHTML=n+liste.innerHTML,nb++}function case1(){var e=document.getElementsByClassName("task");if(done.checked)for(var n=0;n<nb;n++)e[n].checked&&(e[n].parentElement.style.display="none");else for(n=0;n<nb;n++)e[n].checked&&(e[n].parentElement.style.display="")}function case2(){var e=document.getElementsByClassName("task");if(notDone.checked)for(var n=0;n<nb;n++)e[n].checked||(e[n].parentElement.style.display="none");else for(n=0;n<nb;n++)e[n].checked||(e[n].parentElement.style.display="")}var btn=document.getElementById("button"),liste=document.getElementById("liste"),nb=0,done=document.getElementById("case1"),notDone=document.getElementById("case2");btn.addEventListener("click",add),done.addEventListener("click",case1),notDone.addEventListener("click",case2);